import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { useQuery } from 'react-query';

import MCard from './components/Card.js';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';


export default function News() {
    const { isLoading, error, data } = useQuery('ad3aa392a85b4007a3d4c225c5c6177e', () =>
        fetch('https://newsapi.org/v2/everything?q=palestine&pageSize=10&apiKey=ad3aa392a85b4007a3d4c225c5c6177e').then(res =>
            res.json()
        )
    );

    if (isLoading) return <p>Loading...</p>;

    if (error) return <p>An error has occurred: {error.message}</p>;

    const { articles, status } = data;


    return (
        <Col className='px-0 pt-5 ' md={"auto"} >

            <Row gap={4}>
                <Col xs={6} md={5}>
                    <MCard obj={articles[0]} />
                </Col>
                <Col xs={6} md={3}>
                    <Col className='pb-4'>
                        <MCard obj={articles[5]} />
                    </Col>
                    <MCard obj={articles[6]} />
                </Col>
                <Col xs={6} md={4}>
                    <MCard obj={articles[2]} />
                </Col>
            </Row>

        </Col>
    );
}
