import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Card from 'react-bootstrap/Card';

import Moment from 'react-moment';
import { Button } from 'react-bootstrap';


function MCard(props) {

  return (


    <Card as={Button} className='shadow p-0 text-align-left rounded-0 border-0' variant="light">

      {props.obj.image && props.obj.image !== "None"  && (

        <Card.Img variant="top" className='rounded-0' src={props.obj.image} />

      )}

      <Card.Body>

        <Card.Title className='fs-4 fw-600 py-1' >{props.obj.title}</Card.Title>

        <Card.Text className='crop-text-2' >
          {props.obj.description}
        </Card.Text>

        <Row >

          <Col md="auto">
            <Card.Text className='fw-600'>
              {props.obj.author}
            </Card.Text>
          </Col>

          <Col md="auto" className="vl p-0" />

          <Col md="auto">
            <Card.Text >
              <Moment date={props.obj.publishedAt} format="D MMMM YYYY"/>
            </Card.Text>
          </Col>

        </Row>

      </Card.Body>

    </Card>


  )
}

export default MCard;
