import axios from 'axios';
import React, { useState, useEffect } from 'react';
import { useQuery } from 'react-query';

import NewsCard from './Card.js';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';


export default function NewsHeadBoard() {

    const { isLoading, error, data, status } = useQuery('ad3aa392a85b4007a3d4c225c5c6177e', () =>
        fetch('https://newsapi.org/v2/everything?q=palestine&pageSize=10&apiKey=ad3aa392a85b4007a3d4c225c5c6177e').then(res =>
            res.json()
        )
    );

    const [finish ,setFinish] = useState(false)
    const [articles , setArticles] = useState([])


    useEffect(() => {

        if (data != undefined)
        {
            console.log(data)
            setArticles(data.articles)
        }
    },[data])

    useEffect(() => {
        setFinish(articles[0] != undefined)
    },[articles])

    if (isLoading) return <p>Loading...</p>;

    if (error) return <p>An error has occurred: {error.message}</p>;


   if(finish) return (      
        <Col className='px-0 pt-5 ' md={"auto"} >

            <Row gap={4}>
                <Col xs={6} md={5}>

                        <NewsCard obj={articles[0]} />

                </Col>
                <Col xs={6} md={3}>
                    <Col className='pb-4'>
                        {articles && (
                            <NewsCard obj={articles[5]} />
                        )}
                    </Col>
                    {articles && (
                        <NewsCard obj={articles[6]} />
                    )}
                </Col>
                <Col xs={6} md={4}>
                    {articles && (
                        <NewsCard obj={articles[2]} />
                    )}
                </Col>
            </Row>

        </Col>
    );

}
