import React, { useState, useEffect } from 'react';
import { useQuery } from 'react-query';

import NewsCard from './Card.js';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

import Stackcard from './Stack_card.js';



/*
"source": {
"id": "google-news",
"name": "Google News"
},
"author": "Reuters",
"title": "D-Day anniversary 2024: World leaders, veterans, commemorate Normandy landings - Reuters",
"description": null,
"url": "https://news.google.com/rss/articles/CBMidGh0dHBzOi8vd3d3LnJldXRlcnMuY29tL3dvcmxkL2V1cm9wZS93b3JsZC1sZWFkZXJzLXZldGVyYW5zLWNvbW1lbW9yYXRlLWQtZGF5cy04MHRoLWFubml2ZXJzYXJ5LW5vcm1hbmR5LTIwMjQtMDYtMDYv0gEA?oc=5",
"urlToImage": null,
"publishedAt": "2024-06-06T10:15:12Z",
"content": null 
 */

function NewsList(props) {

    const { isLoading, error, data, status } = useQuery('ad3aa392a85b4007a3d4c225c5c6177e', () =>
        fetch('https://newsapi.org/v2/everything?q=palestine&pageSize=10&apiKey=ad3aa392a85b4007a3d4c225c5c6177e').then(res =>
            res.json()
        )
    );

    if (isLoading) return <p>Loading...</p>;

    if (error) return <p>An error has occurred: {error.message}</p>;

    const { articles } = data;

    return (


        <Col className='h-100 py-4' >

            <Row xs={1} md={3} className='g-4' >

                {Array.from({ length: 3 }).map((_, idx) => (
                    <Col key={idx}>
                        <Stackcard articles={articles} i_line={idx} />
                    </Col>
                )
                )}

            </Row>

        </Col>


    )
}

export default NewsList;
